interface ERC20Interface {
  function transferFrom(address from, address to, uint tokens) external returns (bool success);
  function allowance() external;
}


contract NFTDice is ERC721, Ownable {

// Token CLAP
    address public token_addr = 0x9C7CAaDE0cfDf7A70f636F4dD933940B23aB3966;
    IERC20 acceptedToken;
    //Oracle
    uint256 clapValue = 10 ** 7; // 10 ** 8 = 1$
    uint256 priceMintDice = 20 * 10 ** 8;

// Address  
    address public manager;
    address public addressForPool = 0x35F77d33A67c48E954F30134911F0961a0BB83bF;

// Definition
    struct Dice {
        uint8 rare; //0-255
        uint8 tipo;
        uint8 [] faces;
    }

    uint256 nextId = 0;

    mapping( uint256 => Dice) private _tokenDetails;

    uint256 [] private _tokenMarket;





    constructor(string memory name, string memory symbol) ERC721(name, symbol) {
            manager = msg.sender;

            acceptedToken = IERC20(token_addr);
    }
    
    function setClapValue(uint256 newClapValue) public onlyOwner {
        clapValue = newClapValue;
    }



//Consultores

    function getClapValue() public view returns (uint256) {
        return clapValue;
    }

    function getTokenDetails(uint256 tokenId) public view returns (Dice memory) {
        return _tokenDetails[tokenId];
    }

    function getAllTokensForUser(address user) public view returns (uint256[] memory) {
        uint256 tokenCount = balanceOf(user);
        if (tokenCount == 0) {
            return new uint256[](0);
        } else {
            uint[] memory result = new uint256[](tokenCount);
            uint256 totalDices = nextId;
            uint256 resultIndex = 0;
            uint256 i;
            for (i = 0; i < totalDices; i++) {
                if (ownerOf(i) == user) {
                    result[resultIndex] = i;
                    resultIndex++;
                }
            }
            return result;
        }
    }


