//Modificadores

    function mint() external payable {
        uint256 amountClap = priceMintDice / clapValue;
        require(amountClap <= acceptedToken.balanceOf(msg.sender), "You need to buy more Clap!");
        require(acceptedToken.transferFrom(msg.sender, addressForPool, amountClap),"transfer Failed");

        uint8[] memory faces = new uint8[](2); faces[0] = 0; faces[1] = 1;
        _tokenDetails[nextId] = Dice(uint8(random() % 6), uint8(random() % 3), faces);
        _safeMint(msg.sender, nextId);
        nextId++;
    }

