// MARKETPLACE
    function buy(uint256 tokenId) public {
        //approve(msg.sender, tokenId);
       // setApprovalForAll(ownerOf(tokenId), true);
        _transfer(address(this), msg.sender, tokenId);
      //  transfer(msg.sender, _amount);
    }

    function sell(uint256 tokenId) public {
        _tokenMarket.push(tokenId);
        transferFrom(msg.sender, address(this), tokenId);
    }

    function getAllTokensForMarket() public view returns (uint256[] memory) {
        return _tokenMarket;
    }


    mapping(address => mapping(uint256 => Listing)) public listings;

    struct Listing {
        uint256 price;
        address seller;
    }

    function addListing(uint256 price, uint256 tokenId) public {
        require(balanceOf(msg.sender) > 0, "caller must own given token");
      //  require(isApprovedForAll(msg.sender, address(this)), "contract must be approved");
    
        listings[msg.sender][tokenId] = Listing(price, msg.sender);
    }

    function purchase(uint256 tokenId) public payable {
        address contractAddr = ownerOf(tokenId);
        Listing memory item = listings[contractAddr][tokenId];
        require(msg.value >= item.price, "insufficient funds sent");

        safeTransferFrom(item.seller, msg.sender, tokenId);
    }




